import React from 'react'

import { Container } from '../../Container/Container'
import { Flex } from '../../Flex/Flex'

import { useTheme } from '../../ThemeContext'
import theme from '../../theme/theme'

export function ColorSwatch({ label, color1, color2, swatch }) {
  const color1Css = swatch
    ? `
    background: ${theme.color[swatch]};
    height: 100px;
    width: 100px;
  `
    : `
    background: ${theme.color[color1]};
    height: 100px;
    width: 50px;
  `
  return (
    <Container
      css={`
        text-align: center;
        margin: 5px;
      `}
      marginBottom={4}>
      <Flex
        elevate={5}
        css={`
          width: 100px;
          margin: 24px auto 8px auto;
        `}>
        <Container css={color1Css} />
        {swatch ? null : (
          <Container
            css={`
            background: ${theme.color[color2]}
            height: 100px;
            width: 50px;
            display: inline-block
            `}
          />
        )}
      </Flex>
      {label}
    </Container>
  )
}
