import React, { useEffect, useMemo, useState } from 'react'
import PropTypes from 'prop-types'

import { Base } from '../Base'

import { notifyNotificationTheme } from './notify.theme'

export const Notification = React.memo(function Notification({
  children,
  className,
  ...propsRest
}) {
  const classNames = `Notify ${className || ''}`
  const themed = useMemo(() => [notifyNotificationTheme], [
    notifyNotificationTheme,
  ])
  const [enter, setEnter] = useState(false)

  const animate = {
    enter: {
      y: 0,
      opacity: 1,
      delay: 300,
    },
    exit: {
      y: -500,
      opacity: 0,
      transition: { duration: 300 },
    },
  }

  useEffect(() => {
    setTimeout(() => {
      setEnter(true)
    }, 300)
  }, [enter])

  // useEffect(() => {
  //   setTimeout(() => {
  //     setEnter(false)
  //   }, 300)
  // }, [enter])

  return (
    <Base
      state={enter ? 'enter' : 'exit'}
      animate={animate}
      element="li"
      className={classNames}
      themed={themed}
      elevate={5}
      {...propsRest}>
      {children}
      <button>X</button>
    </Base>
  )
})
