import React, { useContext, useMemo } from 'react'
import PropTypes from 'prop-types'
import styled from 'styled-components'

import { useAccordion } from './AccordionContext'
import { useAccordionItem } from './AccordionItem'
import { Base } from '../Base'
import { ThemeContext } from '../ThemeContext'
import { accordionContentTheme } from './accordion.theme'

export const AccordionContent = React.memo(function AccordionContent({
  children,
  className,
  ...propsRest
}) {
  const classNames = `AccordionButton ${className || ''}`
  const themed = useMemo(() => [accordionContentTheme], [accordionContentTheme])

  const { activeItem } = useAccordion()
  const { uid } = useAccordionItem()

  console.log(uid, activeItem)

  return (
    <Base
      className={classNames}
      themed={themed}
      css={activeItem == uid ? 'display: block;' : 'display: none;'}
      {...propsRest}>
      {children}
    </Base>
  )
})

AccordionContent.propTypes = {
  children: PropTypes.any.isRequired,
  theme: PropTypes.object,
}

export const accordionContent = styled(AccordionContent)
export default AccordionContent
