import React, { useMemo } from 'react'

import { menuContentTheme } from './menu.theme'
import { Base } from '../Base'
import { Portal } from '../Portal'

export const MenuContent = React.memo(function MenuContent({
  children,
  className,
  menuRef,
  opened,
  animate,
  ...propsRest
}) {
  const classNames = `MenuContent ${className || ''}`
  const themed = useMemo(() => [menuContentTheme], [menuContentTheme])

  const _animate = useMemo(() => {
    return (
      animate || {
        opened: {
          scale: 1,
          opacity: 1,
          delay: 500,
          duration: 500,
        },
        closed: {
          scale: 0,
          opacity: 0,
        },
      }
    )
  }, [propsRest.animate])

  return opened ? (
    <Base
      className={classNames}
      element="ul"
      themed={themed}
      {...propsRest}
      animate={_animate}>
      {children}
    </Base>
  ) : null
})
