export function buttonIconTheme({
  size,
  theme: { animation, border, color, unit },
}) {
  return {
    position: 'relative',
    whiteSpace: 'nowrap',
    transition: animation.easeFast(),
    lineHeight: 'inherit',
    '&:hover': {
      color: color.action,
    },
    ...(size ? { height: size, width: size, padding: 0 } : {}),

    '>svg.Icon': {
      transform: 'translateY(4px)',
      ...(size ? { height: size, width: size } : {}),
    },
  }
}

