export function appBarTheme({ theme: { color, radius, unit } }) {
  return {
    alignItems: 'center',
    boxSizing: 'border-box',
    display: 'flex',
    display: 'flex',
    flexShrink: 0,
    minHeight: '56px',
    position: 'relative',
    width: '100%',
    zIndex: '1100',

    paddingLeft: unit[4],
    paddingRight: unit[4],

    input: {
      background: 'hsla(0,0%,100%,0.3)',
      borderRadius: '1px solid #0000',
      '&::placeholder': {
        color: color['grey-050'],
      },
      '&:hover': {
        background: 'hsla(0,0%,100%,0.25)',
      },
      '&:focus': {
        background: 'hsla(0,0%,100%,0.15)',
      },
      '&:active': {
        background: 'hsla(0,0%,100%,0.15)',
      },
    },
  }
}

export function appBarAppearanceTheme({ appearance, theme: { color } }) {
  return (
    {
      action: {
        backgroundColor: color.action,
        color: '#ffffff',
      },
      accent: {
        background: color.accent,
        color: '#ffffff',
      },
      danger: {
        backgroundColor: color.danger,
        color: '#ffffff',
      },
      success: {
        backgroundColor: color.success,
        color: '#ffffff',
      },
      warning: {
        backgroundColor: color.warning,
        color: '#ffffff',
      },
    }[appearance] || ''
  )
}
