  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */
import { Playground, Props } from 'docz'
import Container from '../../Container/Container'
import Flex from '../../Flex/Flex'
import { ThemeProvider } from '../../ThemeContext'
import theme from '../../theme/theme'

const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <h1 {...{
      "id": "units"
    }}>{`Units`}</h1>
    <ThemeProvider mdxType="ThemeProvider">
  <Container css={`
  text-align: left;
  table {
    width: 100%;
  }
  td {
    padding-top: 24px;
  }
  `} mdxType="Container">
    <table>
      <thead>
      <tr>
      <th>Value</th>
      <th>px</th>
      <th>Example</th>
  </tr>
  </thead>

  <tbody>
    <tr>
      <td>default</td>
      <td>{theme.unit['default']}</td>
      <td>
        <Container css={`
            width: ${theme.unit['default']};
            height: ${theme.unit['default']};
            background: #777;
          `} mdxType="Container" />
      </td>
    </tr>
    {Object.keys(theme.unit).reduce((acc, key) => {
              const border = key === 0 ? '' : `border: 1px solid ${theme.color['grey-500']};`;
              return key === 'default' ? acc : [...acc, <tr key={key}>
          <td>{key}</td>
          <td>{theme.unit[key]}</td>
          <td>
            <Container css={`
                width: ${theme.unit[key]};
                height: ${theme.unit[key]};
                ${border}
              `} mdxType="Container" />
          </td>
        </tr>];
            }, [])}




  </tbody>
        </table>
  </Container>
    </ThemeProvider>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
