    noscript {
      margin: 0  0 ${unit.default} 0;
      padding: 0;
    }
    iframe {
      margin: 0  0 ${unit.default} 0;
      padding: 0;
    }
    hr {
      margin: 0;
      padding: 0;
      margin-bottom: calc(${unit.default}  - 1px);
      background: hsla(0, 0%, 0%, 0.2);
      border: none;
      height: 1px;
    }
    address {
      margin: 0  0 ${unit.default} 0;
      padding: 0;
    }
    b {
      font-weight: bold;
    }
    strong {
      font-weight: bold;
    }
    dt {
      font-weight: bold;
    }
    th {
      font-weight: bold;
    }
    li {
      margin-bottom: calc(${unit.default} / 2);
    }
    ol li {
      padding-left: 0;
    }
    ul li {
      padding-left: 0;
    }
    li > ol {
      margin-left: ${unit.default};
      margin-bottom: calc(${unit.default} / 2);
      margin-top: calc(${unit.default} / 2);
    }
    li > ul {
      margin-left: ${unit.default};
      margin-bottom: calc(${unit.default} / 2);
      margin-top: calc(${unit.default} / 2);
    }
    blockquote *:last-child {
      margin-bottom: 0;
    }
    li *:last-child {
      // margin-bottom: 0;
    }
    p *:last-child {
      margin#root,
      .react-monaco-editor-container,
      .overflow-guard {
        height: 100vh !important;
      }
    }
    li > p {
      margin-bottom: calc(${unit.default} / 2);
    }
    abbr {
      border-bottom: 1px dotted hsla(0, 0%, 0%, 0.5);
      cursor: help;
    }
    acronym {
      border-bottom: 1px dotted hsla(0, 0%, 0%, 0.5);
      cursor: help;
    }
    abbr[title] {
      border-bottom: 1px dotted hsla(0, 0%, 0%, 0.5);
      cursor: help;
      text-decoration: none;
    }
    thead {
      text-align: left;
    }
    td,
    th {
      text-align: left;
      border-bottom: 1px solid hsla(0, 0%, 100%, 0.12);
      font-feature-settings: 'tnum';
      -moz-font-feature-settings: 'tnum';
      -ms-font-feature-settings: 'tnum';
      -webkit-font-feature-settings: 'tnum';
      padding-left: ${unit[3]};
      padding-right: ${unit[3]};
      padding-top: ${unit[3]};
      padding-bottom: ${unit[3]};
    }
    th:first-child,
    td:first-child {
      padding-left: 0;
    }
    th:last-child,
    td:last-child {
      padding-right: 0;
    }
    tt,
    code {
      background-color: hsla(0, 0%, 0%, 0.04);
      border-radius: 3px;
      font-family: 'SFMono-Regular', Consolas, 'Roboto Mono', 'Droid Sans Mono',
        'Liberation Mono', Menlo, Courier, monospace;
      padding: 0;
      padding-top: ${unit[1]};
      padding-bottom: ${unit[1]};
    }
    pre code {
      background: none;
      line-height: ${font.lineHeight.default};
    }
    code:before,
    code:after,
    tt:before,
    tt:after {
      letter-spacing: -${unit[1]};
      content: ' ';
    }
    pre code:before,
    pre code:after,
    pre tt:before,
    pre tt:after {
      content: '';
    }
    @media only screen and (max-width: 480px) {
      html {
        font-size: 100%;
      }
    }
    `}
  `

  return <Global theme={theme} />
