export function toolbarTheme({ borders, theme: { border, color, unit } }) {
  return {
    paddingLeft: 0,
    paddingRight: 0,
    minHeight: 'auto',

    '>button': {
      color: color.foreground,
      background: 'none',
      borderColor: color.transparent,
      borderRadius: 0,
    },
  }
}
