export function notifyTheme({
  theme: { animation, border, color, font, unit },
}) {
  return {
    position: 'fixed',
    top: unit[3],
    left: unit[3],

    lineHeight: font.lineHeight.default,
    cursor: 'pointer',
    borderRadius: border.radius,
    zIndex: 1040,
    width: '350px',
    transition: animation.easeMedium(),
  }
}

export function notifyPositionTheme({
  position,
  theme: { border, color, font, unit },
}) {
  return (
    {
      'top-center': {
        left: '50%',
        right: 'auto',
      },
      'top-left': {
        left: unit[3],
        right: 'auto',
      },
      'top-right': {
        right: unit[3],
        left: 'auto',
      },
      'bottom-center': {
        left: '50%',
        right: 'auto',
      },
      'bottom-left': {
        top: unit[3],
        left: unit[3],
        right: 'auto',
      },
      'bottom-right': {
        top: 0,
        right: unit[3],
        left: 'auto',
      },
    }[position] || ''
  )
}
