export function notifyNotificationTheme({ theme: { color, elevate } }) {
  return {
    position: 'relative',
    background: color.background,
    width: '300px',
    minHeight: '50px',
    marginTop: '5px',
    zIndex: '100',
    boxShadow: elevate[5],
  }
}

export function notifyCloseButtonTheme({
  theme: { animation, border, color, font, unit },
}) {
  return {
    position: 'absolute',
    top: 0,
    right: 0,
    background: 'none',
    borderColor: '#0000',
  }
}
