export function appBarTheme({ theme: { color, radius, unit } }) {
  return {
    alignItems: 'center',
    boxSizing: 'border-box',
    display: 'flex',
    display: 'flex',
    flexShrink: 0,
    minHeight: '56px',
    position: 'relative',
    width: '100%',
    zIndex: '1100',
    backgroundColor: color.backgroundInvert,
    color: color.foregroundInvert,

    '>button.Button': {
      minHeight: 'inherit',
      border: '1px solid #0000',
      '>svg.Icon': {
        paddingRight: 0,
      },
    },

    'input.Input': {
      background: 'hsla(0,0%,0%,0.1)',
      border: '1px solid #0000',
      borderRadius: radius.rounded,
      color: color.foregroundInvert,

      '&::placeholder': {
        color: color['grey-800'],
      },
      '&:hover': {
        background: 'hsla(0,0%,0%,0.2)',
      },
      '&:focus': {
        background: 'hsla(0,0%,0%,0.2)',
      },
      '&:active': {
        background: 'hsla(0,0%, 0%,0.2)',
      },
    },
  }
}

export function appBarAppearanceTheme({ appearance, theme: { color } }) {
  const placeholder = {
    'input.Input': {
      color: color.foreground,
      '&::placeholder': {
        color: color['grey-050'],
      },
    },
  }
  return (
    {
      action: {
        backgroundColor: color.action,
        color: '#ffffff',
        ...placeholder,
      },
      accent: {
        background: color.accent,
        color: '#ffffff',
        ...placeholder,
      },
      danger: {
        backgroundColor: color.danger,
        color: '#ffffff',
        ...placeholder,
      },
      success: {
        backgroundColor: color.success,
        color: '#ffffff',
        ...placeholder,
      },
      warning: {
        backgroundColor: color.warning,
        color: '#ffffff',
        ...placeholder,
      },
    }[appearance] || ''
  )
}
