import React, { useEffect, useMemo, useRef, useState } from 'react'
import ReactDOM from 'react-dom'
import styled from 'styled-components'
import PropTypes from 'prop-types'
import posed, { PoseGroup } from 'react-pose'

import Button from '../Button/Button'
import Base from '../Base/Base'
import { Portal } from '../Portal'
import {
  notifyTheme,
  notifyPositionTheme,
  notifyNotificationTheme,
} from './notify.theme'

export const Notifications = React.memo(function Notifications({
  animate,
  children,
  className,
  notification,
  onClose,
  themed: propsThemed,
  ...propsRest
}) {
  const classNames = `Notifications ${className || ''}`
  const themed = useMemo(
    () => [notifyTheme, notifyPositionTheme, ...propsThemed],
    [notifyTheme, notifyPositionTheme, propsThemed]
  )

  const rootRef = useRef(document.getElementsByTagName('body')[0])

  const _animate = useMemo(() => {
    return (
      animate || {
        enter: {
          y: 0,
          transition: {
            y: { type: 'spring', stiffness: 1000, damping: 100 },
            default: { duration: 300 },
          },
        },
        exit: {
          y: -500,
          transition: { duration: 300 },
        },
      }
    )
  }, [animate])

  const items = notification
    .map(({ id, component }) => {
      return (
        <Base
          key={id}
          css={`
            background: grey;
            width: 300px;
            height: 50px;
            margin-top: 5px;
            z-index: 1000;
          `}
          animate={_animate}>
          <Button onClick={onClose} data-uid={id}>
            Close
          </Button>
          {component}
        </Base>
      )
    })
    .reverse()

  return (
    <Portal rootRef={rootRef}>
      <Base className={classNames} element="ul" themed={themed} {...propsRest}>
        <PoseGroup>{items}</PoseGroup>
      </Base>
    </Portal>
  )
})

Notifications.defaultProps = {
  themed: [],
}

Notifications.propTypes = {
  children: PropTypes.any,
  classes: PropTypes.array,
  className: PropTypes.string,
  items: PropTypes.object,
  uid: PropTypes.string,
  position: PropTypes.oneOf([
    'bottom-center',
    'bottom-left',
    'bottom-right',
    'top-center',
    'top-left',
    'top-right',
  ]),
  messages: PropTypes.array,
  themed: PropTypes.array,
}

export default Notifications
