export function typographyTheme({ theme: { animation } }) {
  return {
    transition: animation.easeMedium(),

    /* =====================================
     * Align
     * =================================== */

    /* =====================================
     * Vertical
     * =================================== */
  }
}

export function typographyContextTheme({ context, theme: { color } }) {
  return (
    {
      accent: {
        color: color.accent,
      },
      action: {
        color: color.action,
      },
      danger: {
        color: color.danger,
      },
      disabled: {
        color: color.secondary,
      },

      success: {
        color: color.success,
      },
      warning: {
        color: color.warning,
      },
    }[context] || ''
  )
}

export function typographyElementTheme({ as, theme: { color, border, unit } }) {
  return (
    {
      code: {
        background: color.secondary,
        color: color.primary,
        padding: '0 4px',
        whiteSpace: 'nowrap',
      },
      hr: {
        border: border.thin,
        margin: `calc(${unit.default} - 1px) 0`,
        textShadow: 'none',
      },
      q: {
        fontStyle: 'italic',
        '&:before': {
          content: ' open-quote',
        },
        '&:after': {
          content: ' close-quote',
        },
      },
      sm: {
        fontSize: '80%',
      },
      strong: {
        fontWeight: 700,
      },
    }[as] || ''
  )
}

export function typographyModifyTheme({ modify, theme: { color, font } }) {
  return (
    {
      lead: {
        fontSize: font.x1,
        lineHeight: 1.5,
        fontWeight: 300,
      },
      meta: {
        fontSize: font.small,
        color: color.secondary,
        lineHeight: 1.4,
      },
    }[modify] || ''
  )
}

export function typographyWrapTheme({ wrap }) {
  return (
    {
      truncate: {
        overflow: 'hidden',
        textOverflow: 'ellipsis',
        whiteSpace: 'nowrap',
      },
      break: {
        wordWrap: 'break-word',
        hyphens: 'auto',
      },
      nowrap: {
        whiteSpace: 'nowrap',
      },
    }[wrap] || ''
  )
}

export function typographyTransformTheme({ textTransform }) {
  return (
    {
      capitalize: {
        textTransform: 'capitalize',
      },
      lowercase: {
        textTransform: 'lowercase',
      },
      uppercase: {
        textTransform: 'uppercase',
      },
    }[textTransform] || ''
  )
}
