import React, { useEffect, useRef, useState } from 'react'
import Button from '../Button/Button'
import Container from '../Container/Container'
import cuid from 'cuid'

export function NotifyContainer({ children }) {
  const [messages, addMessage] = useState([])
  const cntRef = useRef(1)

  function handleClick(e) {
    e.preventDefault()

    addMessage([
      {
        id: cuid(),
        message: `${e.target.dataset.sticky ? 'Stick ' : ''} Item ${
          cntRef.current
        }`,
        sticky: e.target.dataset.sticky,
      },
    ])

    cntRef.current = cntRef.current + 1
  }

  return (
    <div>
      <Container margin5="bottom">
        <Button onClick={handleClick}>Add notification</Button>
      </Container>

      <Container margin5="bottom">
        <Button data-sticky={true} onClick={handleClick}>
          Sticky notification
        </Button>
      </Container>
      {children(messages)}
    </div>
  )
}
