export function notifyTheme({
  theme: { animation, border, color, font, unit },
}) {
  return {
    position: 'fixed',
    top: unit[3],
    left: unit[3],

    lineHeight: font.lineHeight.default,
    cursor: 'pointer',
    borderRadius: border.radius,
    zIndex: 1040,
    width: '350px',
    transition: animation.easeMedium(),
  }
}

export function notifyPositionTheme({
  position,
  theme: { border, color, font, unit },
}) {
  return (
    {
      'top-center': {
        left: '50%',
        right: 'auto',
      },
      'top-left': {
        left: unit[3],
        right: 'auto',
      },
      'top-right': {
        right: unit[3],
        left: 'auto',
      },
      'bottom-center': {
        left: '50%',
        right: 'auto',
      },
      'bottom-left': {
        top: unit[3],
        left: unit[3],
        right: 'auto',
      },
      'bottom-right': {
        top: 0,
        right: unit[3],
        left: 'auto',
      },
    }[position] || ''
  )
}
// element.style {
//   display: block;
//   opacity: 1;
//   transform: translateY(0px);
// }
// .uk-notify-message {
//   position: relative;
//   margin-bottom: 10px;
//   padding: 15px;
//   background: #444;
//   color: #fff;
//   font-size: 16px;
//   line-height: 22px;
//   cursor: pointer;
//   border: 1px solid #444;
//   border-radius: 4px;
// }

export function notifyNotificationTheme({ theme: { color, elevate, unit } }) {
  return {
    position: 'relative',
    background: color.background,
    width: '300px',
    minHeight: '50px',
    marginTop: '5px',
    zIndex: '100',
    boxShadow: elevate[5],
    padding: unit[5],
  }
}

export function notifyNotificationCloseButtonTheme({
  theme: { animation, border, color, font, unit },
}) {
  return {
    position: 'absolute',
    top: 0,
    right: 0,
    background: 'none',
    borderColor: '#0000',
    color: color.grey300,

    '&:hover': {
      background: 'inherit',
      borderColor: '#0000',
      color: color.foreground,
    },

    '&:active': {
      background: 'inherit',
      borderColor: '#0000',
      color: color.active,
    },
  }
}

export function notifyNotificationAppearanceTheme({
  appearance,
  context,
  theme: { color, border, radius },
  theme,
  ...rest
}) {
  return {
    secondary: {
      border: border.thinGrey200,
      color: color.foreground,
    },
    tertiary: {
      borderLeft: `1px solid ${color.foreground}`,
      color: color.foreground,
    },

    accent: {
      background: color.accent,
      border: border.thinAccent,
    },
    'primary-accent': {
      background: color.accent,
      border: border.thinAccent,
    },
    'secondary-accent': {
      border: border.thinAccent,
    },
    'tertiary-accent': {
      borderLeft: border.thickAccent,
    },

    action: {
      background: color.action,
      border: border.thinAction,
      color: color.foreground,
    },

    'primary-action': {
      background: color.action,
      border: border.thinAction,
      color: color.foreground,
    },
    'secondary-action': {
      background: 'none',
      border: border.thinAction,
      color: color.foreground,
    },
    'tertiary-action': {
      background: 'none',
      border: `1px solid ${color.transparent}`,
      color: color.action,
    },

    danger: {
      background: color.danger,
      border: border.thinDanger,
      color: color.foreground,
    },
    'primary-danger': {
      background: color.danger,
      border: border.thinDanger,
      color: color.foreground,
    },
    'secondary-danger': {
      background: 'none',
      border: border.thinDanger,
      color: color.foreground,
    },
    'tertiary-danger': {
      background: 'none',
      border: `1px solid ${color.transparent}`,
      color: color.danger,
      borderRadius: 0,
    },

    success: {
      background: color.success,
      border: border.thinSuccess,
      color: color.foreground,
    },
    'primary-success': {
      background: color.success,
      border: border.thinSuccess,
      color: color.foreground,
    },
    'secondary-success': {
      background: 'none',
      border: border.thinSuccess,
      color: color.foreground,
    },
    'tertiary-success': {
      background: 'none',
      border: `1px solid ${color.transparent}`,
      color: color.success,
      borderRadius: 0,
    },

    warning: {
      background: color.warning,
      border: border.thinWarning,
      color: color.foreground,
    },
    'primary-warning': {
      background: color.warning,
      border: border.thinWarning,
      color: color.foreground,
    },
    'secondary-warning': {
      background: 'none',
      border: border.thinWarning,
      color: color.foreground,
    },
    'tertiary-warning': {
      background: 'none',
      border: `1px solid ${color.transparent}`,
      color: color.warning,
    },
  }[appearance]
}
